# ---------- Insert data ----------
def add_products():
    db: Session = SessionLocal()
    try:
        for pdata in products_data:
            product = Product(**pdata)
            db.add(product)
        db.commit()
        print(f"{len(products_data)} products added successfully!")
    except Exception as e:
        db.rollback()
        print("Error adding products:", e)
    finally:
        db.close()

if __name__ == "__main__":
    add_products()





















